import React from 'react'
import {Box,Grid,FilledInput,Select,MenuItem,Dialog,DialogTitle,DialogContent,DialogActions, Typography,makeStyles, Button,IconButton, CircularProgress} from '@material-ui/core'
import {Close as CloseIcon} from '@material-ui/icons'
import {format} from 'date-fns';

const useStyles = makeStyles({
    info:{
        '& > *':{
            margin:'5px',
    },
},

});

export default (props) =>{
    const classes = useStyles()
    return (
    <Dialog open={!!Object.keys(props.job).length} fullWidth>
        <DialogTitle>
                <Box display="flex" justifyContent="space-between" alignItems="center">
                    {props.job.title} as {props.job.companyName}
                    <IconButton>
                        <CloseIcon/>
                    </IconButton>
                </Box>
            </DialogTitle>
            <DialogContent>
                <Box>
                    <Box className={classes.info} display="flex">
                        <Typography variant="caption">Posted on</Typography>
                        <Typography variant="caption">{props.job.postedOn && format(props.job.postedOn ,"dd/MMM/yyyy  HH:MM")}</Typography>

                    </Box>
                </Box>
            </DialogContent>
            <DialogActions></DialogActions>
    </Dialog>
    )
}
